import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const EditAccount = (props) => {

    const {show, handleClose, dataEditAccount, handleAccountEdit} = props

    const handleEditAccount = async () => {
        // let res = await editAccount(name, phone)
        // if (res && res.updatedAt) {
        //     handleAccountEdit({
        //         name: name,
        //         id: dataEditAccount.id
        //     })
        //     handleClose()
        //     toast.success("Update successful!!!")
        // }
    }

    return(
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
            <Modal.Title>Edit Accounts</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="body-edit">
                    <div className="mb-3">
                        <label className="form-label">First Name</label>
                        <input type="text" className="form-control"/>
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Last Name</label>
                        <input type="text" className="form-control"/>
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Email</label>
                        <input type="text" className="form-control"/>
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Password</label>
                        <input type="text" className="form-control"/>
                    </div>
                    <select class="select-form">
                        <option selected>Select role</option>
                        <option value="1">Student</option>
                        <option value="2">Manager</option>
                        <option value="3">Admin</option>
                    </select>
                    <div class="mb-3">
                        <label for="formFile" class="form-label">Image</label>
                        <input class="form-control" type="file" id="formFile"/>
                    </div>
                </div>
            </Modal.Body>
            <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
                Close
            </Button>
            <Button variant="primary" onClick={() => handleEditAccount()}>
                Confirm
            </Button>
            </Modal.Footer>
        </Modal>
    )
}

export default EditAccount
