import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { addNewAccount, fetchAllFaculty } from '../../service/userService';
import { useEffect, useState } from 'react';
import { toast } from 'react-toastify';

const AddAccount = (props) => {

    const {show, handleClose, handleAddNewAccount} = props
    const [userData, setUserData] = useState({
        name:"",
        password:""
    })
    const [listFaculty, setListFaculty] = useState([])


  
    const getAllFaculty = async () =>{
        let res = await fetchAllFaculty()
        if (res) {
            setListFaculty(res.data)
            
        }
    }
    useEffect(() => {
        getAllFaculty()
    }, [])
    const handleChange=(e)=>{
        const {name,value}=e.target;
        setUserData({...userData,[name]:value})
    }
    return(
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
            <Modal.Title>New Account</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="body-add">
                    <div className="mb-3">
                        <label className="form-label">Name</label>
                        <input type="text" className="form-control" name='name' value={userData}  onChange={handleChange}/>
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Email</label>
                        <input type="text" className="form-control" name='email' onChange={handleChange}/>
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Password</label>
                        <input type="text" name='password' className="form-control" onChange={handleChange}/>
                    </div>
                    <select className="form-select" name='faculty' onChange={handleChange}>
                        {listFaculty&&listFaculty.map((faculty)=>{
                            return  <option key={faculty._id} value={faculty._id}>{faculty.faculty_name}</option>
                        })}
                       
                       
                    </select>
                    <select className="form-select" name='role' onChange={handleChange}>
                        <option value={"student"}>Student</option>
                        <option value={"admin"}>Admin</option>
                        <option value={"marketing manage"}>Marketing Manage</option>
                        <option value={"marketing coordinator"}>Marketing Coordinator</option>
                    </select>
                    <br/>
                
                    <div className="mb-3">
                        <label htmlFor="formFile" className="form-label">Image</label>
                        <input className="form-control" type="file" id="formFile"/>
                    </div>
                </div>
            </Modal.Body>
            <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
                Close
            </Button>
            <Button variant="primary" onClick={() => handleSaveAccount()}>
                Save
            </Button>
            </Modal.Footer>
        </Modal>
    )
}

export default AddAccount
