import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const ModalEdit = (props) => {

    const {show, handleClose, dataEditAccount, handleEventEdit} = props
    const [data, setData] = useState(dataEditAccount)

    const handleChange = (e) => {
        setData({ ...data, faculty_name: e.target.value })
    }
    useEffect(() => {
        if (dataEditAccount) {
            setData(dataEditAccount)
        }
    }, [dataEditAccount])

    return(
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
            <Modal.Title>Edit Event</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="body-edit">
                <div className="mb-3">
                        <label className="form-label">Event Name</label>
                        <input type="text" className="form-control" value={data.event_name && data.faculty_name} onChange={handleChange}/>
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Deadline of event</label>
                        <input type="text" className="form-control"/>
                    </div>
                </div>
            </Modal.Body>
            <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
                Close
            </Button>
            <Button variant="primary" onClick={() => handleEventEdit(data)}>
                Confirm
            </Button>
            </Modal.Footer>
        </Modal>
    )
}

export default ModalEdit
