import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { useState } from 'react';

const ModalAdd = (props) => {

    const { show, handleClose, handleAddNewEvent } = props
    const [data, setData] = useState({
        "event_name": "",
        "first_closure_date": ""
        // "phone": "",
        // "email": "",
        // "password": "",
        // "dateOfBirth": "",
        // "gender": "female",
        // "role": "customer",
    })

    const handleSaveAccount = async () => {
        // let res = await addNewAccount(userData)
        // if (res && res.id) {
        //     handleClose()
        //     setUserData('')
        //     toast.success("Create new account successful!!!")
        //     handleAddNewAccount({userData})
        // }else{
        //     toast.error("Fail to create new account")
        // }
    }

    return(
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
            <Modal.Title>New Event</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div className="body-add">
                <div className="mb-3">
                        <label className="form-label">Event Name</label>
                        <input type="text" className="form-control"/>
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Date start</label>
                        <input type="text" className="form-control"/>
                    </div>
                    <div className="mb-3">
                        <label className="form-label">Deadline of event</label>
                        <input type="text" className="form-control"/>
                    </div>
                </div>
            </Modal.Body>
            <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
                Close
            </Button>
            <Button variant="primary" onClick={() => handleSaveAccount()}>
                Save
            </Button>
            </Modal.Footer>
        </Modal>
    )
}

export default ModalAdd
