import React, { useEffect, useState } from "react";
import "../../style/adminEvent.css"
import ModalAdd from "./modalAdd";
import ModalEdit from "./modalEdit";
import ModalDelete from "./modalDelete";
import { addNewEvent, fetchAllEvent } from "../../service/userService";

const Event = () => {

    const [isShowModalAdd, setIsShowModalAdd] = useState(0)
    const [isShowModalEdit, setIsShowModalEdit] = useState(false)
    const [isShowModalDelete, setIsShowModalDelete] = useState(false)
    const [dataEdit, setDataEdit] = useState({})
    const [dataDelete, setDataDelete] = useState({})
    const [listEvent, setListEvent] = useState([])
    const [totalEvent, setTotalEvent] = useState(0)

    const handleClose = () => {
        setIsShowModalAdd(false)
        setIsShowModalEdit(false)
        setIsShowModalDelete(false)
    }
    const handleAddNew = async (account) => {
        try {
            const response = await addNewEvent(account);
            if (response) {
                await getAllFaculty()
                setIsShowModalAdd(!isShowModalAdd)
                toast.success("Create success!")
            }
        } catch (error) {
            toast.error("Create fail")
        }
    }
    const handleEdit = (accountEdit) => {
        setDataEdit(accountEdit)
        setIsShowModalEdit(true)
    }
    const handleDelete = (accountDelete) => {
        setIsShowModalDelete(true)
        setDataDelete(accountDelete)
    }
    const handleEditFromModal = (user) => {
        // let cloneListAccount = [...listAccount]
        // let index = listAccount.findIndex(item => item.id === user.id)
        // cloneListAccount[index].name = user.name
        // setListAccount(cloneListAccount)
    }
    const handleDEleteFromModal = (user) =>{
        // let cloneListAccount = [...listAccount]
        // cloneListAccount = cloneListAccount.filter(item => item._id !== user._id)
        // setListAccount(cloneListAccount)
    }

    const getAllEvent = async () =>{
        let res = await fetchAllEvent()
        if (res) {
            setTotalEvent(res.total)
            setListEvent(res.data)
            // setTotalPages(res.total_pages)
        }
    }

    useEffect(() => {
        getAllEvent()
    }, [])

    return(
        <>
        <div className="event-manage">
            
                <div className="button-container">
                    <button className="btn btn-primary"
                    onClick={() => setIsShowModalAdd(true)}
                    >Add Event</button>
                </div>
                <div className="event-form">
                    <h3>List Event</h3>
                    <div className="list-event">
                        <div className="eventM">
                            <div className="assignment">
                                <span>Assignment 1</span>
                                <div>Status</div>
                            </div>
                            <div className="deadline">
                                <span>Deadline of event</span>
                                <div>Date</div>
                            </div>
                            <div className="grd-container">
                                <div className="grd-item">Post</div>
                                <div className="grd-item">Like</div>
                                <div className="grd-item">Comment</div>
                                <div className="grd-item">Post number</div>
                                <div className="grd-item">Like number</div>
                                <div className="grd-item">Comment number</div>
                            </div>
                            <div className="button">
                                <button
                                    className="btn btn-warning"
                                    onClick={() => handleEdit()}
                                >Edit</button>
                                <button
                                    className="btn btn-danger"
                                    onClick={() => handleDelete()}
                                >Delete</button>
                            </div>
                        </div>
                        <div className="eventM">
                            <div className="assignment">
                                <span>Assignment 1</span>
                                <div>Status</div>
                            </div>
                            <div className="deadline">
                                <span>Deadline of vent</span>
                                <div>Date</div>
                            </div>
                            <div className="grd-container">
                                <div className="grd-item">Post</div>
                                <div className="grd-item">Like</div>
                                <div className="grd-item">Comment</div>
                                <div className="grd-item">Post number</div>
                                <div className="grd-item">Like number</div>
                                <div className="grd-item">Comment number</div>
                            </div>
                            <div className="button">
                                <button className="btn btn-warning">Edit</button>
                                <button className="btn btn-danger">Delete</button>
                            </div>
                        </div>
                        <div className="eventM">
                            <div className="assignment">
                                <span>Assignment 1</span>
                                <div>Status</div>
                            </div>
                            <div className="deadline">
                                <span>Deadline of event</span>
                                <div>Date</div>
                            </div>
                            <div className="grd-container">
                                <div className="grd-item">Post</div>
                                <div className="grd-item">Like</div>
                                <div className="grd-item">Comment</div>
                                <div className="grd-item">Post number</div>
                                <div className="grd-item">Like number</div>
                                <div className="grd-item">Comment number</div>
                            </div>
                            <div className="button">
                                <button
                                    className="btn btn-warning"
                                    onClick={() => handleEdit()}
                                >Edit</button>
                                <button className="btn btn-danger">Delete</button>
                            </div>
                        </div>
                    </div>
                </div>
            
        </div>
        <ModalAdd
            show = {isShowModalAdd}
            handleClose = {handleClose}
            handleAddNewAccount = {handleAddNew}
        />
        <ModalEdit
            show = {isShowModalEdit}
            dataEditAccount = {dataEdit}
            handleClose = {handleClose}
            handleAccountEdit = {handleEditFromModal}
        />
        <ModalDelete
            show = {isShowModalDelete}
            handleClose = {handleClose}
            dataUserDelete = {dataDelete}
            handleAccountDelete = {handleDEleteFromModal}
        />
        </>
    )
}

export default Event
